import styled from "styled-components";

export const Container = styled.div`
    background: #f3fdff;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-around;
    height: 100vh;
`;

export const SearchBar = styled.div`
    background: #d2e5e9;
    height: 100px;
    width: 97%;
    border-radius: 10px;
    display: flex;
    align-items: center;
    justify-content: space-around;
`;

export const SearchTitle = styled.span`
    font-size: 30px;
    font-family: "Montserrat", sans-serif;
    font-style: normal;
    font-weight: bold;
    line-height: 37px;
    float: left;
`;

export const SearchInput = styled.input`
    background: #f0fcff;
    border-radius: 10px;
    width: 75%;
    height: 61px;
    border: none;
    padding-left: 10px;
    ::-webkit-input-placeholder {
        font-size: 20px;
        margin: 10px;
    }
`;

export const CitiesMetropolis = styled.div`
    width: 47%;
    height: 80%;
    background: #d2e5e9;
    border-radius: 10px;
    @media (max-width: 768px) {
        width: 97%;
        margin: 10px 0 10px 0;
    }
`;

export const CitiesTitle = styled.span`
    font-family: Montserrat;
    font-weight: bold;
    font-size: 30px;
    line-height: 37px;
    padding-top: 2px;
    width: 100%;
`;

export const ContainerCities = styled.div`
    display: flex;
    align-items: center;
    flex-wrap: wrap;
    justify-content: space-around;
    width: 100%;
`

export const CardCity = styled.div`
    background: rgba(22, 28, 41, 0.63);
    height: 60px;
    width: 45%;
`

export const OverseasCities = styled.div`
    width: 47%;
    height: 80%;
    background: #d2e5e9;
    border-radius: 10px;
    @media (max-width: 768px) {
        width: 97%;
    }
`;

export const OvereasTitle = styled.span`
    font-family: Montserrat;
    font-weight: bold;
    font-size: 30px;
    line-height: 37px;
    padding-top: 2px;
`;
