import React from "react";
import styled from 'styled-components'

export const Container = styled.div`
    background: #F3FDFF;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-around;
    height: 100vh;
`

export const SearchBar = styled.div`
    background: #D2E5E9;
    height: 100px;
    width: 97%;
    border-radius: 10px;
    display: flex;
    align-items: center;
    justify-content: space-around;
`

export const SearchTitle = styled.span`
    font-size:30px;
    font-family: 'Montserrat', sans-serif;
    font-style: normal;
    font-weight: bold;
    line-height: 37px;
    float: left;
`

export const SearchInput = styled.input`
    background: #F0FCFF;
    border-radius: 10px;
    width: 75%;
    height: 61px;
    border: none;
    ::-webkit-input-placeholder {
        font-size: 30px;
        padding-top: 30px;
  }

`

export const CitiesAndMetropolis = styled.div`
    width:47%;
    height: 80%;
    background: #D2E5E9;
    border-radius: 10px;
    @media (max-width: 768px) {
    width: 97%;
    margin: 10px 0 10px 0;
  }
`

export const OverseasCities = styled.div`
    width:47%;
    height: 80%;
    background: #D2E5E9;
    border-radius: 10px;
    @media (max-width: 768px) {
    width: 97%;
  }
`
