import React, {useState} from 'react'
import {Container, SearchBar, CitiesMetropolis, OverseasCities,SearchTitle,SearchInput,CitiesTitle,OvereasTitle} from './CitiesElements'
import axios from 'axios'

const CitiesSearch = () => {
    const [datas, setDatas] = useState()
    const searchInput = ''
    const [searchData,setSearchData] = useState(searchInput)

    const handleChange = (e) => {
        setSearchData(e.target.value)
        axios({
            method: "get",
            url: "http://localhost:8080/cities/all",
            params: {data: e.target.value},
        }).then((response) => setDatas(response.data));

    }

    return (
        <Container>
            <SearchBar>
                <SearchTitle>Je recherche... </SearchTitle>
                <SearchInput 
                 type="text"
                 onChange={handleChange}
                 value={searchInput}
                 id="search"
                 placeholder=" ...une ville, un code postal"
                />
            </SearchBar>
            <CitiesMetropolis>
                <CitiesTitle> villes de metropole</CitiesTitle>
               <p>{datas}</p>
            </CitiesMetropolis>
            <OverseasCities>
                <OvereasTitle>ville d&apos;outre-mer</OvereasTitle>
            </OverseasCities>
        </Container>
    )
}

export default CitiesSearch
